import React from "react";
import { useEffect } from 'react';


function Weather() {
  useEffect(() => {
    // Weather widget script initialization
    !function(d,s,id){
      var js,fjs=d.getElementsByTagName(s)[0];
      if(!d.getElementById(id)){
        js=d.createElement(s);
        js.id=id;
        js.src='https://weatherwidget.io/js/widget.min.js';
        fjs.parentNode.insertBefore(js,fjs);
      }
    }(document,'script','weatherwidget-io-js');
  }, []); // Empty dependency array ensures this runs once after the component mounts

  return (
    <div className="h-5 p-5 bg-body-tertiary border rounded-3">
    <a className="weatherwidget-io" href="https://forecast7.com/en/40d71n74d01/new-york/" data-label_1="NEW YORK" data-label_2="WEATHER" data-theme="beige">
      NEW YORK WEATHER
    </a>
    </div>
  );
}

export default Weather;
