import React, {useState} from "react";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCheckbox,
  MDBCol,
  MDBContainer,
  MDBIcon,
  MDBListGroup,
  MDBListGroupItem,
  MDBRow,
  MDBTextArea,
  MDBTooltip,
} from "mdb-react-ui-kit";
import ChecklistForm from "./ChecklistForm";
import TaskList from "./TaskList";

function Checklist(props) {
    // store users' todo tasks
    const [tasks, setTasks] = useState(["first", "second", "third", "fourth", "fifth"]);

    // add new task to the original tasks list
    const addTask = (newTask) => {
        setTasks([...tasks, newTask]);
      };



  return (
    <div className="col-md-7">
      <div className="h-100 p-10 text-bg-dark rounded-3">
        <section className="vh-100">
          <MDBContainer className="py-5 h-100">
            <MDBRow className="d-flex justify-content-center align-items-center">
              <MDBCol xl="10">
                <MDBCard style={{ borderRadius: "15px" }}>
                  <MDBCardBody className="p-5">
                    <h6 className="checklist-title mb-3">Todo List</h6>

                    <ChecklistForm addTask={addTask}/>
                    <TaskList tasks = {tasks} />

                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
            </MDBRow>
          </MDBContainer>
        </section>
      </div>
    </div>
  );
}

export default Checklist;
