import React, { useState } from "react";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCheckbox,
  MDBCol,
  MDBContainer,
  MDBIcon,
  MDBListGroup,
  MDBListGroupItem,
  MDBRow,
  MDBTextArea,
  MDBTooltip,
} from "mdb-react-ui-kit";

function ChecklistForm(props) {

  const [task, setTask] = useState("");

  function handleInputChange(event) {
    setTask(event.target.value);
  }
  function handleSubmit() {
    if (task.trim() !== '') {
        props.addTask(task); // Call the addTask function from props to update the state in the parent component
        setTask(''); // Reset the task input after submitting
      }
  }

  return (
    <div className="d-flex justify-content-center align-items-center mb-4">
      <MDBTextArea
        onChange={handleInputChange}
        id="checklist-form"
        rows={4}
        wrapperClass="flex-fill"
        placeholder="Add a new todo"
        value={task}
      />
      <MDBBtn color='warning'>Warning</MDBBtn>
    </div>
  );
}

export default ChecklistForm;
